shacl_profile_suggestions:
# SHACL Profile for general-document documents
kb:GeneralDocumentProfile a sh:NodeShape ;
    sh:targetNode [ sh:property [ sh:path kb:info_type ; sh:hasValue "general-document" ] ] ;
    sh:property [
        sh:path kb:info_type ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have info_type field." ;
    ] ;
    sh:property [
        sh:path kb:title ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have title field." ;
    ] ;
    sh:property [
        sh:path kb:date_modified ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have date_modified field." ;
    ] ;
    sh:property [
        sh:path kb:date_created ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have date_created field." ;
    ] ;
    sh:property [
        sh:path kb:kb_id ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have kb_id field." ;
    ] ;
    sh:property [
        sh:path kb:version ; 
        sh:minCount 1 ;
        sh:message "general-document documents must have version field." ;
    ] ;
    sh:property [
        sh:path kb:standard_id ; 
        sh:maxCount 0 ;
        sh:message "general-document documents must not have standard_id field." ;
    ] .

